import { object } from 'prop-types';
import Card from './components/Card';


const Titre = styled.h2`
color: #fff;
font-weight: 300;
`

const Date = styled.div`
color: #ccc;
font-weight: 300;
margin: 6px 0;
`

const Description = styled.p`
color: #fff;
font-weight: 300;
`
// Styled navbar
const Title = styled.h1`
      font-size: 1.5em;
      text-align: center;
      color: palevioletred;`;
const Search = styled.input`
      border-radius:10px;
      margin: auto;
      width: 35%;
      height:3em;
      background-color: #202224;
      border-style: none;
      text-align: center;
      color:white; `;
const Wrapper = styled.section`
      padding: 1em;
      background: Black;
      height:5em;
      box-shadow: 0px 5px 15px #323234;
      display: flex; `;
const Logo = styled.p`
      font-size:1.5em;  
      margin-left:2em;
      margin-top: auto;
      margin-bottom: auto; `;
 const Profile = styled.img`
      width:3em;
      margin-right:3em; `;

const link = 'https://api.themoviedb.org/3/search/movie?api_key=7f1a9ab21c04fc25c73f0115df15f92e&language=en-US&query=ride&page=1&include_adult=false'

export class App extends Component {

  state = {
    movies:[],
    title:'',
    overview:'',
    date:''
  }

  handleSubmit = () => {
    axios.get(link)
      .then(res => {
        const movies = res.data.results;
        console.log({movies})
        this.setState({ movies });
      })

    }

     handleNameChange = event  => {
       var code = event.keycde || event.which;
       if (code ===13){
         this.handleSubmit()
       }
     }


  render() {

      const movies = Object
      .keys(this.state.movies)
      .map(key => 
        <Movie key={key}
        title={this.state.movies[key].original_title}
        overview={this.state.movies[key].overview}
        date={this.state.movies[key].release_date}
        />

        )

    return (
      <div className="App">
               <Wrapper>
                     <Logo>VÃ–KKA</Logo>
                     <Search placeholder="Maybe Avatar?"></Search>
                    <Profile src="https://cdn0.iconfinder.com/data/icons/kameleon-free-pack-rounded/110/Woman-15-512.png"></Profile>
                </Wrapper>
                <button onClick={this.handleSubmit}>submit</button>
                {movies}

            <Titre>spiderma</Titre>
