border: none;
padding:1em;
padding-right:2em;
padding-left:2em;
font-weight:700;

`;
const Container = styled.div`


`;


export class Healine extends Component {
    render() {
        return (
           <div>
              <Bg>
                <Container>  

                <Title> NOW ON STREAM </Title>     
                   <Button>VIEW SHOWTIME</Button>

                   </Container>


              </Bg>


           </div>
        )
    }
}

export default Healine
