import React, { Component } from 'react'
import logo from './logo.svg';
import './App.css';
import styled from 'styled-components';
import axios from 'axios';
import Movie from './components/Movie';
import { object } from 'prop-types';
import Healine from './components/Healine';

// Styled navbar
const Title = styled.h1`
      font-size: 1.5em;
      text-align: center;
      color: palevioletred;`;
const Search = styled.input`
      border-radius:10px;
      margin: auto;
      width: 35%;
      height:3em;
      background-color: #202224;
      border-style: none;
      text-align: center;
      color:white; `;
const Wrapper = styled.section`
      padding: 1em;
      background: Black;
      height:5em;
      box-shadow: 0px 5px 15px #323234;
      display: flex; `;
const Logo = styled.p`
      font-size:1.5em;  
      margin-left:2em;
      margin-top: auto;
      margin-bottom: auto; `;
 const Profile = styled.img`
      width:3em;
      margin-right:3em; `;

const Container = styled.section`
      padding: 1em;
      display: flex; 
      flex-wrap: wrap; 
      display: -webkit-flex;
      display: -ms-flexbox;
      `;



const start = 'https://api.themoviedb.org/3/search/movie?api_key=7f1a9ab21c04fc25c73f0115df15f92e&language=en-US&query='
const end = '&page=1&include_adult=false'



export class App extends Component {

  state = {
    query:'',
    movies:[],
    title:'',
    overview:'',
    date:'',
    poster:''
  }

  handleChange = event => {
    this.setState({ query : event.target.value}, () => {
        console.log(this.state.query)
    })

}

  handleSubmit = () => {
    const link = start.concat(this.state.query,end);
    axios.get(link)
      .then(res => {
        const movies = res.data.results;
        this.setState({ movies });
      })
      this.setState({...this.state.query})

    }
    onEnterPress = (e) => {
      if(e.keyCode == 13 && e.shiftKey == false) {
        e.preventDefault();
        this.handleSubmit();
      }
    }

  render() {
      const path ='https://image.tmdb.org/t/p/w500'
      const movies = Object
      .keys(this.state.movies)
      .map(key => 
        <Movie key={key}
        title={this.state.movies[key].original_title}
        overview={this.state.movies[key].overview}
        date={this.state.movies[key].release_date}
        poster= {this.state.movies[key].poster_path}
        />

        )

    return (
      <div className="App">
               <Wrapper>
                     <Logo>VÃ–KKA</Logo>
                     <Search  type ='search' onChange={this.handleChange} placeholder="Maybe Avatar ?" onKeyDown={this.onEnterPress}></Search>
                    <Profile src="https://cdn0.iconfinder.com/data/icons/kameleon-free-pack-rounded/110/Woman-15-512.png"></Profile>
                </Wrapper>
                <Healine></Healine>
                <Container>{movies}</Container>
                <img src="/images/background.jpg"></img>




      </div>
    );
  }
}

export default App
